import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"

export interface AbortXferData {
	xferId: {
		id: number | bigint
		result: number
	}
}

export const id = 157
export const name = "AbortXfer"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "xferId" entry (fixed-length parameters only)
const XFER_ID_BASE_SIZE =
	Primitives.U64.size() + // id
	Primitives.S32.size() // result

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + XFER_ID_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<AbortXferData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// XferId
	offset = Primitives.U64.encode(data.xferId.id, buffer, offset)
	offset = Primitives.S32.encode(data.xferId.result, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): AbortXferData {
	const state = { offset: HEADER_SIZE }

	return {
		xferId: {
			id: Primitives.U64.decode(buffer, state),
			result: Primitives.S32.decode(buffer, state),
		},
	}
}
