const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() + // sessionId
	Primitives.U32.size() // circuitCode

// base byte size for one "throttle" entry (fixed-length parameters only)
