import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface ChatFromViewerData {
	agentData?: {
		agentId?: Types.UUID
		sessionId?: Types.UUID
	}
	chatData: {
		message: Buffer
		type: number
		channel: number
	}
}

export const id = 80
export const name = "ChatFromViewer"
export const frequency = 2
export const compression = true

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentData" entry (fixed-length parameters only)
const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() // sessionId

// base byte size for one "chatData" entry (fixed-length parameters only)
const CHAT_DATA_BASE_SIZE =
	Primitives.U8.size() + // type
	Primitives.S32.size() // channel

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + AGENT_DATA_BASE_SIZE + CHAT_DATA_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<ChatFromViewerData>) {
	let size = BASE_SIZE

	size += Primitives.Variable2.size(data.chatData.message)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// AgentData
	offset = Primitives.UUID.encode(data.agentData.agentId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentData.sessionId, buffer, offset)

	// ChatData
	offset = Primitives.Variable2.encode(data.chatData.message, buffer, offset)
	offset = Primitives.U8.encode(data.chatData.type, buffer, offset)
	offset = Primitives.S32.encode(data.chatData.channel, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): ChatFromViewerData {
	const state = { offset: HEADER_SIZE }

	return {
		agentData: {
			agentId: Primitives.UUID.decode(buffer, state),
			sessionId: Primitives.UUID.decode(buffer, state),
		},
		chatData: {
			message: Primitives.Variable2.decode(buffer, state),
			type: Primitives.U8.decode(buffer, state),
			channel: Primitives.S32.decode(buffer, state),
		},
	}
}
