import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface CrossedRegionData {
	agentData?: {
		agentId?: Types.UUID
		sessionId?: Types.UUID
	}
	regionData: {
		simIp: Types.IP
		simPort: Types.Port
		regionHandle: number | bigint
		seedCapability: Buffer
	}
	info: {
		position: Types.Vector3
		lookAt: Types.Vector3
	}
}

export const id = 7
export const name = "CrossedRegion"
export const frequency = 1

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentData" entry (fixed-length parameters only)
const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() // sessionId

// base byte size for one "regionData" entry (fixed-length parameters only)
const REGION_DATA_BASE_SIZE =
	Primitives.IP.size() + // simIp
	Primitives.Port.size() + // simPort
	Primitives.U64.size() // regionHandle

// base byte size for one "info" entry (fixed-length parameters only)
const INFO_BASE_SIZE =
	Primitives.Vector3.size() + // position
	Primitives.Vector3.size() // lookAt

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + AGENT_DATA_BASE_SIZE + REGION_DATA_BASE_SIZE + INFO_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<CrossedRegionData>) {
	let size = BASE_SIZE

	size += Primitives.Variable2.size(data.regionData.seedCapability)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// AgentData
	offset = Primitives.UUID.encode(data.agentData.agentId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentData.sessionId, buffer, offset)

	// RegionData
	offset = Primitives.IP.encode(data.regionData.simIp, buffer, offset)
	offset = Primitives.Port.encode(data.regionData.simPort, buffer, offset)
	offset = Primitives.U64.encode(data.regionData.regionHandle, buffer, offset)
	offset = Primitives.Variable2.encode(data.regionData.seedCapability, buffer, offset)

	// Info
	offset = Primitives.Vector3.encode(data.info.position, buffer, offset)
	offset = Primitives.Vector3.encode(data.info.lookAt, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): CrossedRegionData {
	const state = { offset: HEADER_SIZE }

	return {
		agentData: {
			agentId: Primitives.UUID.decode(buffer, state),
			sessionId: Primitives.UUID.decode(buffer, state),
		},
		regionData: {
			simIp: Primitives.IP.decode(buffer, state),
			simPort: Primitives.Port.decode(buffer, state),
			regionHandle: Primitives.U64.decode(buffer, state),
			seedCapability: Primitives.Variable2.decode(buffer, state),
		},
		info: {
			position: Primitives.Vector3.decode(buffer, state),
			lookAt: Primitives.Vector3.decode(buffer, state),
		},
	}
}
