	offset = Primitives.UUID.encode(data.agentBlock.destinationId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentBlock.transactionId, buffer, offset)
	offset = Primitives.U8.encode(data.agentBlock.packetCount, buffer, offset)
	offset = Primitives.U8.encode(data.agentBlock.packetNumber, buffer, offset)

