import type * as Types from "~/model/types"

export interface FormFriendshipData {
	agentBlock: {
		sourceId: Types.UUID
		destId: Types.UUID
	}
}

export const id = 299
export const name = "FormFriendship"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentBlock" entry (fixed-length parameters only)
const AGENT_BLOCK_BASE_SIZE =
	Primitives.UUID.size() + // sourceId
	Primitives.UUID.size() // destId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + AGENT_BLOCK_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<FormFriendshipData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// AgentBlock
	offset = Primitives.UUID.encode(data.agentBlock.sourceId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentBlock.destId, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): FormFriendshipData {
	const state = { offset: HEADER_SIZE }

	return {
		agentBlock: {
			sourceId: Primitives.UUID.decode(buffer, state),
			destId: Primitives.UUID.decode(buffer, state),
		},
	}
}
