import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"

export interface GenericStreamingMessageData {
	methodData: {
		method: number
	}
	dataBlock: {
		data: Buffer
	}
}

export const id = 31
export const name = "GenericStreamingMessage"
export const frequency = 0

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "methodData" entry (fixed-length parameters only)
const METHOD_DATA_BASE_SIZE = Primitives.U16.size() // method

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + METHOD_DATA_BASE_SIZE + DATA_BLOCK_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<GenericStreamingMessageData>) {
	let size = BASE_SIZE

	size += Primitives.Variable2.size(data.dataBlock.data)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// MethodData
	offset = Primitives.U16.encode(data.methodData.method, buffer, offset)

	// DataBlock
	offset = Primitives.Variable2.encode(data.dataBlock.data, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): GenericStreamingMessageData {
	const state = { offset: HEADER_SIZE }

	return {
		methodData: {
			method: Primitives.U16.decode(buffer, state),
		},
		dataBlock: {
			data: Primitives.Variable2.decode(buffer, state),
		},
	}
}
