import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface InternalScriptMailData {
	dataBlock: {
		from: Buffer
		to: Types.UUID
		subject: Buffer
		body: Buffer
	}
}

export const id = 16
export const name = "InternalScriptMail"
export const frequency = 1

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "dataBlock" entry (fixed-length parameters only)
const DATA_BLOCK_BASE_SIZE = Primitives.UUID.size() // to

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + DATA_BLOCK_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<InternalScriptMailData>) {
	let size = BASE_SIZE

	size += Primitives.Variable1.size(data.dataBlock.from)
	size += Primitives.Variable1.size(data.dataBlock.subject)
	size += Primitives.Variable2.size(data.dataBlock.body)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// DataBlock
	offset = Primitives.Variable1.encode(data.dataBlock.from, buffer, offset)
	offset = Primitives.UUID.encode(data.dataBlock.to, buffer, offset)
	offset = Primitives.Variable1.encode(data.dataBlock.subject, buffer, offset)
	offset = Primitives.Variable2.encode(data.dataBlock.body, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): InternalScriptMailData {
	const state = { offset: HEADER_SIZE }

	return {
		dataBlock: {
			from: Primitives.Variable1.decode(buffer, state),
			to: Primitives.UUID.decode(buffer, state),
			subject: Primitives.Variable1.decode(buffer, state),
			body: Primitives.Variable2.decode(buffer, state),
		},
	}
}
