import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface LargeGenericMessageData {
	agentData: {
		agentId?: Types.UUID
		sessionId?: Types.UUID
		transactionId: Types.UUID
	}
	methodData: {
		method: Buffer
		invoice: Types.UUID
	}
	paramList: {
		parameter: Buffer
	}[]
}

export const id = 430
export const name = "LargeGenericMessage"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentData" entry (fixed-length parameters only)
const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() + // sessionId
	Primitives.UUID.size() // transactionId

// base byte size for one "methodData" entry (fixed-length parameters only)
const METHOD_DATA_BASE_SIZE = Primitives.UUID.size() // invoice

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + AGENT_DATA_BASE_SIZE + METHOD_DATA_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<LargeGenericMessageData>) {
	let size = BASE_SIZE

	size += Primitives.Variable1.size(data.methodData.method)

	// add 1 byte for the block count
	size += Primitives.U8.size()

	for (const item of data.paramList ?? []) {
		size += Primitives.Variable2.size(item.parameter)
	}

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// AgentData
	offset = Primitives.UUID.encode(data.agentData.agentId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentData.sessionId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentData.transactionId, buffer, offset)

	// MethodData
	offset = Primitives.Variable1.encode(data.methodData.method, buffer, offset)
	offset = Primitives.UUID.encode(data.methodData.invoice, buffer, offset)

	// ParamList
	offset = Primitives.U8.encode(data.paramList?.length ?? 0, buffer, offset)

	for (const item of data.paramList ?? []) {
		offset = Primitives.Variable2.encode(item.parameter, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): LargeGenericMessageData {
	const state = { offset: HEADER_SIZE }

	return {
		agentData: {
			agentId: Primitives.UUID.decode(buffer, state),
			sessionId: Primitives.UUID.decode(buffer, state),
			transactionId: Primitives.UUID.decode(buffer, state),
		},
		methodData: {
			method: Primitives.Variable1.decode(buffer, state),
			invoice: Primitives.UUID.decode(buffer, state),
		},
		paramList: (() => {
			const items: LargeGenericMessageData["paramList"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					parameter: Primitives.Variable2.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
