import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface MergeParcelData {
	masterParcelData: {
		masterId: Types.UUID
	}
	slaveParcelData: {
		slaveId: Types.UUID
	}[]
}

export const id = 223
export const name = "MergeParcel"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "masterParcelData" entry (fixed-length parameters only)
const MASTER_PARCEL_DATA_BASE_SIZE = Primitives.UUID.size() // masterId

// base byte size for one "slaveParcelData" entry (fixed-length parameters only)
const SLAVE_PARCEL_DATA_BASE_SIZE = Primitives.UUID.size() // slaveId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + MASTER_PARCEL_DATA_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<MergeParcelData>) {
	let size = BASE_SIZE

	// add 1 byte for the block count
	size += Primitives.U8.size()

	size += SLAVE_PARCEL_DATA_BASE_SIZE * (data.slaveParcelData?.length ?? 0)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// MasterParcelData
	offset = Primitives.UUID.encode(data.masterParcelData.masterId, buffer, offset)

	// SlaveParcelData
	offset = Primitives.U8.encode(data.slaveParcelData?.length ?? 0, buffer, offset)

	for (const item of data.slaveParcelData ?? []) {
		offset = Primitives.UUID.encode(item.slaveId, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): MergeParcelData {
	const state = { offset: HEADER_SIZE }

	return {
		masterParcelData: {
			masterId: Primitives.UUID.decode(buffer, state),
		},
		slaveParcelData: (() => {
			const items: MergeParcelData["slaveParcelData"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					slaveId: Primitives.UUID.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
