import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface NetTestData {
	netBlock: {
		port: Types.Port
	}
}

export const id = 326
export const name = "NetTest"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "netBlock" entry (fixed-length parameters only)
const NET_BLOCK_BASE_SIZE = Primitives.Port.size() // port

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + NET_BLOCK_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<NetTestData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// NetBlock
	offset = Primitives.Port.encode(data.netBlock.port, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): NetTestData {
	const state = { offset: HEADER_SIZE }

	return {
		netBlock: {
			port: Primitives.Port.decode(buffer, state),
		},
	}
}
