import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface ObjectAnimationData {
	sender: {
		id: Types.UUID
	}
	animationList: {
		animId: Types.UUID
		animSequenceId: number
	}[]
}

export const id = 30
export const name = "ObjectAnimation"
export const frequency = 0

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "sender" entry (fixed-length parameters only)
const SENDER_BASE_SIZE = Primitives.UUID.size() // id

// base byte size for one "animationList" entry (fixed-length parameters only)
const ANIMATION_LIST_BASE_SIZE =
	Primitives.UUID.size() + // animId
	Primitives.S32.size() // animSequenceId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + SENDER_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<ObjectAnimationData>) {
	let size = BASE_SIZE

	// add 1 byte for the block count
	size += Primitives.U8.size()

	size += ANIMATION_LIST_BASE_SIZE * (data.animationList?.length ?? 0)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// Sender
	offset = Primitives.UUID.encode(data.sender.id, buffer, offset)

	// AnimationList
	offset = Primitives.U8.encode(data.animationList?.length ?? 0, buffer, offset)

	for (const item of data.animationList ?? []) {
		offset = Primitives.UUID.encode(item.animId, buffer, offset)
		offset = Primitives.S32.encode(item.animSequenceId, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): ObjectAnimationData {
	const state = { offset: HEADER_SIZE }

	return {
		sender: {
			id: Primitives.UUID.decode(buffer, state),
		},
		animationList: (() => {
			const items: ObjectAnimationData["animationList"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					animId: Primitives.UUID.decode(buffer, state),
					animSequenceId: Primitives.S32.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
