import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface OpenCircuitData {
	circuitInfo: {
		ip: Types.IP
		port: Types.Port
	}
}

export const id = 4294967292
export const name = "OpenCircuit"
export const frequency = 3

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "circuitInfo" entry (fixed-length parameters only)
const CIRCUIT_INFO_BASE_SIZE =
	Primitives.IP.size() + // ip
	Primitives.Port.size() // port

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + CIRCUIT_INFO_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<OpenCircuitData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// CircuitInfo
	offset = Primitives.IP.encode(data.circuitInfo.ip, buffer, offset)
	offset = Primitives.Port.encode(data.circuitInfo.port, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): OpenCircuitData {
	const state = { offset: HEADER_SIZE }

	return {
		circuitInfo: {
			ip: Primitives.IP.decode(buffer, state),
			port: Primitives.Port.decode(buffer, state),
		},
	}
}
