export const id = 209
export const name = "ParcelClaim"
export const frequency = 2
export const compression = true

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentData" entry (fixed-length parameters only)
const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() // sessionId

// base byte size for one "data" entry (fixed-length parameters only)
const DATA_BASE_SIZE =
	Primitives.UUID.size() + // groupId
	Primitives.Boolean.size() + // isGroupOwned
	Primitives.Boolean.size() // final

// base byte size for one "parcelData" entry (fixed-length parameters only)
const PARCEL_DATA_BASE_SIZE =
	Primitives.F32.size() + // west
	Primitives.F32.size() + // south
	Primitives.F32.size() + // east
	Primitives.F32.size() // north

// size contributed by the packet header and all FIXED-LENGTH fields
