import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"

export interface ParcelOverlayData {
	parcelData: {
		sequenceId: number
		data: Buffer
	}
}

export const id = 196
export const name = "ParcelOverlay"
export const frequency = 2
export const compression = true

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "parcelData" entry (fixed-length parameters only)
const PARCEL_DATA_BASE_SIZE = Primitives.S32.size() // sequenceId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + PARCEL_DATA_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<ParcelOverlayData>) {
	let size = BASE_SIZE

	size += Primitives.Variable2.size(data.parcelData.data)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// ParcelData
	offset = Primitives.S32.encode(data.parcelData.sequenceId, buffer, offset)
	offset = Primitives.Variable2.encode(data.parcelData.data, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): ParcelOverlayData {
	const state = { offset: HEADER_SIZE }

	return {
		parcelData: {
			sequenceId: Primitives.S32.decode(buffer, state),
			data: Primitives.Variable2.decode(buffer, state),
		},
	}
}
