import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface ScriptQuestionData {
	data: {
		taskId: Types.UUID
		itemId: Types.UUID
		objectName: Buffer
		objectOwner: Buffer
		questions: number
	}
	experience: {
		experienceId: Types.UUID
	}
}

export const id = 188
export const name = "ScriptQuestion"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "data" entry (fixed-length parameters only)
const DATA_BASE_SIZE =
	Primitives.UUID.size() + // taskId
	Primitives.UUID.size() + // itemId
	Primitives.S32.size() // questions

// base byte size for one "experience" entry (fixed-length parameters only)
const EXPERIENCE_BASE_SIZE = Primitives.UUID.size() // experienceId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + DATA_BASE_SIZE + EXPERIENCE_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<ScriptQuestionData>) {
	let size = BASE_SIZE

	size += Primitives.Variable1.size(data.data.objectName)
	size += Primitives.Variable1.size(data.data.objectOwner)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// Data
	offset = Primitives.UUID.encode(data.data.taskId, buffer, offset)
	offset = Primitives.UUID.encode(data.data.itemId, buffer, offset)
	offset = Primitives.Variable1.encode(data.data.objectName, buffer, offset)
	offset = Primitives.Variable1.encode(data.data.objectOwner, buffer, offset)
	offset = Primitives.S32.encode(data.data.questions, buffer, offset)

	// Experience
	offset = Primitives.UUID.encode(data.experience.experienceId, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): ScriptQuestionData {
	const state = { offset: HEADER_SIZE }

	return {
		data: {
			taskId: Primitives.UUID.decode(buffer, state),
			itemId: Primitives.UUID.decode(buffer, state),
			objectName: Primitives.Variable1.decode(buffer, state),
			objectOwner: Primitives.Variable1.decode(buffer, state),
			questions: Primitives.S32.decode(buffer, state),
		},
		experience: {
			experienceId: Primitives.UUID.decode(buffer, state),
		},
	}
}
