import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface SoundTriggerData {
	soundData: {
		soundId: Types.UUID
		ownerId: Types.UUID
		objectId: Types.UUID
		parentId: Types.UUID
		handle: number | bigint
		position: Types.Vector3
		gain: number
	}
}

export const id = 29
export const name = "SoundTrigger"
export const frequency = 0

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "soundData" entry (fixed-length parameters only)
const SOUND_DATA_BASE_SIZE =
	Primitives.UUID.size() + // soundId
	Primitives.UUID.size() + // ownerId
	Primitives.UUID.size() + // objectId
	Primitives.UUID.size() + // parentId
	Primitives.U64.size() + // handle
	Primitives.Vector3.size() + // position
	Primitives.F32.size() // gain

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + SOUND_DATA_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<SoundTriggerData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// SoundData
	offset = Primitives.UUID.encode(data.soundData.soundId, buffer, offset)
	offset = Primitives.UUID.encode(data.soundData.ownerId, buffer, offset)
	offset = Primitives.UUID.encode(data.soundData.objectId, buffer, offset)
	offset = Primitives.UUID.encode(data.soundData.parentId, buffer, offset)
	offset = Primitives.U64.encode(data.soundData.handle, buffer, offset)
	offset = Primitives.Vector3.encode(data.soundData.position, buffer, offset)
	offset = Primitives.F32.encode(data.soundData.gain, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): SoundTriggerData {
	const state = { offset: HEADER_SIZE }

	return {
		soundData: {
			soundId: Primitives.UUID.decode(buffer, state),
			ownerId: Primitives.UUID.decode(buffer, state),
			objectId: Primitives.UUID.decode(buffer, state),
			parentId: Primitives.UUID.decode(buffer, state),
			handle: Primitives.U64.decode(buffer, state),
			position: Primitives.Vector3.decode(buffer, state),
			gain: Primitives.F32.decode(buffer, state),
		},
	}
}
