import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface StartLureData {
	agentData?: {
		agentId?: Types.UUID
		sessionId?: Types.UUID
	}
	info: {
		lureType: number
		message: Buffer
	}
	targetData: {
		targetId: Types.UUID
	}[]
}

export const id = 70
export const name = "StartLure"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "agentData" entry (fixed-length parameters only)
const AGENT_DATA_BASE_SIZE =
	Primitives.UUID.size() + // agentId
	Primitives.UUID.size() // sessionId

// base byte size for one "info" entry (fixed-length parameters only)
const INFO_BASE_SIZE = Primitives.U8.size() // lureType

// base byte size for one "targetData" entry (fixed-length parameters only)
const TARGET_DATA_BASE_SIZE = Primitives.UUID.size() // targetId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + AGENT_DATA_BASE_SIZE + INFO_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<StartLureData>) {
	let size = BASE_SIZE

	size += Primitives.Variable1.size(data.info.message)

	// add 1 byte for the block count
	size += Primitives.U8.size()

	size += TARGET_DATA_BASE_SIZE * (data.targetData?.length ?? 0)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// AgentData
	offset = Primitives.UUID.encode(data.agentData.agentId, buffer, offset)
	offset = Primitives.UUID.encode(data.agentData.sessionId, buffer, offset)

	// Info
	offset = Primitives.U8.encode(data.info.lureType, buffer, offset)
	offset = Primitives.Variable1.encode(data.info.message, buffer, offset)

	// TargetData
	offset = Primitives.U8.encode(data.targetData?.length ?? 0, buffer, offset)

	for (const item of data.targetData ?? []) {
		offset = Primitives.UUID.encode(item.targetId, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): StartLureData {
	const state = { offset: HEADER_SIZE }

	return {
		agentData: {
			agentId: Primitives.UUID.decode(buffer, state),
			sessionId: Primitives.UUID.decode(buffer, state),
		},
		info: {
			lureType: Primitives.U8.decode(buffer, state),
			message: Primitives.Variable1.decode(buffer, state),
		},
		targetData: (() => {
			const items: StartLureData["targetData"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					targetId: Primitives.UUID.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
