import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface TeleportFailedData {
	info: {
		agentId: Types.UUID
		reason: Buffer
	}
	alertInfo: {
		message: Buffer
		extraParams: Buffer
	}[]
}

export const id = 74
export const name = "TeleportFailed"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "info" entry (fixed-length parameters only)
const INFO_BASE_SIZE = Primitives.UUID.size() // agentId

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + INFO_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<TeleportFailedData>) {
	let size = BASE_SIZE

	size += Primitives.Variable1.size(data.info.reason)

	// add 1 byte for the block count
	size += Primitives.U8.size()

	for (const item of data.alertInfo ?? []) {
		size += Primitives.Variable1.size(item.message)
		size += Primitives.Variable1.size(item.extraParams)
	}

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// Info
	offset = Primitives.UUID.encode(data.info.agentId, buffer, offset)
	offset = Primitives.Variable1.encode(data.info.reason, buffer, offset)

	// AlertInfo
	offset = Primitives.U8.encode(data.alertInfo?.length ?? 0, buffer, offset)

	for (const item of data.alertInfo ?? []) {
		offset = Primitives.Variable1.encode(item.message, buffer, offset)
		offset = Primitives.Variable1.encode(item.extraParams, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): TeleportFailedData {
	const state = { offset: HEADER_SIZE }

	return {
		info: {
			agentId: Primitives.UUID.decode(buffer, state),
			reason: Primitives.Variable1.decode(buffer, state),
		},
		alertInfo: (() => {
			const items: TeleportFailedData["alertInfo"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					message: Primitives.Variable1.decode(buffer, state),
					extraParams: Primitives.Variable1.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
