import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface TransferInventoryData {
	infoBlock: {
		sourceId: Types.UUID
		destId: Types.UUID
		transactionId: Types.UUID
	}
	inventoryBlock: {
		inventoryId: Types.UUID
		type: number
	}[]
}

export const id = 295
export const name = "TransferInventory"
export const frequency = 2
export const compression = true

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "infoBlock" entry (fixed-length parameters only)
const INFO_BLOCK_BASE_SIZE =
	Primitives.UUID.size() + // sourceId
	Primitives.UUID.size() + // destId
	Primitives.UUID.size() // transactionId

// base byte size for one "inventoryBlock" entry (fixed-length parameters only)
const INVENTORY_BLOCK_BASE_SIZE =
	Primitives.UUID.size() + // inventoryId
	Primitives.S8.size() // type

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + INFO_BLOCK_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<TransferInventoryData>) {
	let size = BASE_SIZE

	// add 1 byte for the block count
	size += Primitives.U8.size()

	size += INVENTORY_BLOCK_BASE_SIZE * (data.inventoryBlock?.length ?? 0)

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// InfoBlock
	offset = Primitives.UUID.encode(data.infoBlock.sourceId, buffer, offset)
	offset = Primitives.UUID.encode(data.infoBlock.destId, buffer, offset)
	offset = Primitives.UUID.encode(data.infoBlock.transactionId, buffer, offset)

	// InventoryBlock
	offset = Primitives.U8.encode(data.inventoryBlock?.length ?? 0, buffer, offset)

	for (const item of data.inventoryBlock ?? []) {
		offset = Primitives.UUID.encode(item.inventoryId, buffer, offset)
		offset = Primitives.S8.encode(item.type, buffer, offset)
	}

	return buffer
}

export function decode(buffer: Buffer): TransferInventoryData {
	const state = { offset: HEADER_SIZE }

	return {
		infoBlock: {
			sourceId: Primitives.UUID.decode(buffer, state),
			destId: Primitives.UUID.decode(buffer, state),
			transactionId: Primitives.UUID.decode(buffer, state),
		},
		inventoryBlock: (() => {
			const items: TransferInventoryData["inventoryBlock"] = []
			const size = Primitives.U8.decode(buffer, state)

			for (let i = 0; i < size; i++) {
				items.push({
					inventoryId: Primitives.UUID.decode(buffer, state),
					type: Primitives.S8.decode(buffer, state),
				})
			}

			return items
		})(),
	}
}
