import type { DeepRequired } from "ts-essentials"
import * as PacketEncoder from "~/codec/lludp/packet-encoder"
import * as Primitives from "~/codec/primitives"
import type * as Types from "~/model/types"

export interface UseCircuitCodeData {
	circuitCode: {
		code: number
		sessionId: Types.UUID
		id: Types.UUID
	}
}

export const id = 3
export const name = "UseCircuitCode"
export const frequency = 2

const HEADER_SIZE = PacketEncoder.FREQUENCY_OFFSETS[frequency]!

// base byte size for one "circuitCode" entry (fixed-length parameters only)
const CIRCUIT_CODE_BASE_SIZE =
	Primitives.U32.size() + // code
	Primitives.UUID.size() + // sessionId
	Primitives.UUID.size() // id

// size contributed by the packet header and all FIXED-LENGTH fields
const BASE_SIZE =
	// Header
	HEADER_SIZE + CIRCUIT_CODE_BASE_SIZE

export function encode(sequence: number, reliable: boolean, data: DeepRequired<UseCircuitCodeData>) {
	const size = BASE_SIZE

	const buffer = Buffer.allocUnsafe(size)

	PacketEncoder.encodeHeader(buffer, id, frequency, sequence, reliable)

	let offset = HEADER_SIZE

	// CircuitCode
	offset = Primitives.U32.encode(data.circuitCode.code, buffer, offset)
	offset = Primitives.UUID.encode(data.circuitCode.sessionId, buffer, offset)
	offset = Primitives.UUID.encode(data.circuitCode.id, buffer, offset)

	return buffer
}

export function decode(buffer: Buffer): UseCircuitCodeData {
	const state = { offset: HEADER_SIZE }

	return {
		circuitCode: {
			code: Primitives.U32.decode(buffer, state),
			sessionId: Primitives.UUID.decode(buffer, state),
			id: Primitives.UUID.decode(buffer, state),
		},
	}
}
